const net = require("node:net");

const socket = net.createConnection({ host: "localhost", port: 5050 }, () => {
	const buff = Buffer.from(
		"",
		"hex",
	);
	// buff[0] = 12;
	// buff[1] = 34;
	socket.write(buff);
});

socket.on("data", (chunk) => {
	console.log(chunk.toString());
	socket.end();
});

socket.on("end", () => {
	console.log("Connection closed");
});

